import React, { useEffect, useState } from 'react';
import { StarOutlined, StarFilled,UserOutlined } from '@ant-design/icons';
import type { MenuProps } from 'antd';
import { Button, Layout, Menu, Space, Switch, Table, theme } from 'antd';
import { getPokemonData } from './endpoints/pokemon';
import MenuItem from 'antd/es/menu/MenuItem';
import Search from 'antd/es/input/Search';
import { fetchPosts } from './redux/action';
import { useDispatch, useSelector } from 'react-redux';

const { Header, Content, Footer, Sider } = Layout;
const TopLayer: React.FC = () => {
    const [loadin, setIsLoading] = useState(false); 
    const [pokemons, setPokemons] = useState<any>();
    const [firstCopy, setFirstCopy] = useState<any>();


    const {posts, loading} = useSelector((state: any) => state.data); 
    const dispatch : any = useDispatch();
    const changeFavorite = (rec: any) =>{  
          let temp = [...firstCopy];
          temp[rec.id].isfavorite = true;
          setPokemons(temp);
        }
    useEffect(()=>{
        console.log(pokemons)
    })

    const columns =[
        {
            title: 'Name',
            dataIndex: 'name',
            key: 'name',
            sorter: (a : any, b: any) => a.name.localeCompare(b.title)
          },
          {
            title: 'Url',
            dataIndex: 'url',
            key: 'url',
          },
          {
            title: 'Favorite',
            render: (item: any) =>{
                return (
                    <div className='items-center'>
                     <Button onClick={()=>changeFavorite(item)}>{item.isfavorite == true ?  <StarFilled/>: <StarOutlined/>}</Button>
                    </div>
                )
            }
          }
        ];

        const onChange = (checked: boolean) => {

            let temp = pokemons

            if(checked == true)
            {
                setPokemons(pokemons.filter(function(x:any) { return x.isfavorite == true}  ))
            }
            else 
               setPokemons(temp);
          };



    const onSearch = (value: string) => {
          if(value == " " || value == "")
          setPokemons(firstCopy);
          else
          setPokemons(pokemons?.filter((x:any) => (x.name.includes(value))))
    };

    const Filters: React.FC =()=>{
        return(
            <>
               <Search placeholder="Search Pokemons" onSearch={onSearch} style={{ width: 200 }} />
               <Switch style={{float: 'right', marginLeft: '10px'}}  onChange={onChange} className='items-right'/>

               <label style={{float: 'right'}}>Toggle Favorites</label>
            </>
      )
    }

    const getPokemons = () => {  
        dispatch(fetchPosts());
              posts.forEach((value: any, idx: number) => 
               {
                posts[idx].id=idx;
                posts[idx].isfavorite = "false"
               }
              );

              setFirstCopy(posts);
              setPokemons(posts);
              setIsLoading(false);
            };

        useEffect(()=>{
          getPokemons();
            },[loadin])

        const {
           token: { colorBgContainer },
          } = theme.useToken();

        return (
            <Layout>
            <Header style={{ display: 'flex', alignItems: 'center' }}>
                <div style={{color: "white"}} >POKEMONS</div>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <Layout style={{ padding: '24px 0', background: colorBgContainer }}>
                <Sider style={{ background: colorBgContainer }} width={200}>
                    <Menu
                    mode="inline"
                    defaultSelectedKeys={['1']}
                    defaultOpenKeys={['sub1']}
                    style={{ height: '100%' }}

                    >
                        <MenuItem key="all" icon={React.createElement(UserOutlined)} >
                            All Pokemons
                        </MenuItem>
                        <MenuItem key="favorites" icon={React.createElement(UserOutlined)} >
                            Favorites
                        </MenuItem>
                    </Menu>
                </Sider>
                <Content style={{ padding: '0 24px', minHeight: 280 }}>
                    <Filters/>
                    <Table key="table" dataSource={pokemons}  columns={columns} pagination= {false}/>   
                </Content>
                </Layout>
            </Content>

            <button onClick={()=> dispatch(fetchPosts())}>fetch posts</button>


            <Footer style={{ textAlign: 'center' }}>Â©2023 Created by Mintesnot Bekele</Footer>
            </Layout>
        );
        };

        export default TopLayer;
