import React, { useEffect, useState } from 'react';
import { StarOutlined, StarFilled,UserOutlined } from '@ant-design/icons';
import { Button, Layout, Menu, Spin, Switch, Table, theme } from 'antd';
import MenuItem from 'antd/es/menu/MenuItem';
import Search from 'antd/es/input/Search';
import { fetchPosts } from './redux/action';
import { useDispatch, useSelector } from 'react-redux';
import { Link, Route, Routes } from 'react-router-dom';
import ListPokemon from './components/pokemon/list';

const { Header, Content, Footer, Sider } = Layout;
const TopLayer: React.FC = () => {
    const [loadin, setIsLoading] = useState(false); 
    const [pokemons, setPokemons] = useState<any>();
    const [firstCopy, setFirstCopy] = useState<any>();


    const {posts, loading} = useSelector((state: any) => state.data); 
    const dispatch : any = useDispatch();

    const getPokemons = () => {  
              setFirstCopy(posts);
              setIsLoading(false);
            };

        useEffect(()=>{
            dispatch(fetchPosts())
            getPokemons();
            },[loadin]);
        useEffect(()=>{

            setPokemons(posts);

        },[posts])

        const {
           token: { colorBgContainer },
          } = theme.useToken();

        return (
            <Layout>
            <Header style={{ display: 'flex', alignItems: 'center' }}>
                <div style={{color: "white"}} >POKEMONS</div>
            </Header>
            <Content style={{ padding: '0 50px' }}>
                <Layout style={{ padding: '24px 0', background: colorBgContainer }}>
                <Sider style={{ background: colorBgContainer }} width={200}>
                    <Menu
                    mode="inline"
                    defaultSelectedKeys={['1']}
                    defaultOpenKeys={['sub1']}
                    style={{ height: '100%' }}
                    >


                    <MenuItem key="all" icon={React.createElement(UserOutlined)} >


                    <Link to="/"> All Pokemons</Link>
                            </MenuItem>
                        <MenuItem key="favorites" icon={React.createElement(StarOutlined)} >
                        <Link to="/">   Favorites </Link>
                        </MenuItem>

                    </Menu>
                </Sider>
                <Content style={{ padding: '0 24px', minHeight: 280 }}>
                  {/* <ListPokemon/> */}
                  <Routes>
                        <Route path="/" element={<ListPokemon />}>
                        </Route>
                        <Route path="/favorites" element={<ListPokemon />}>
                        </Route>
                    </Routes>
                </Content>
                </Layout>
            </Content>     
            <Footer style={{ textAlign: 'center' }}>Â©2023 Created by Mintesnot Bekele</Footer>
            </Layout>
        );
        };

        export default TopLayer;
