import React, { useEffect, useState } from 'react';
import { StarOutlined, StarFilled,UserOutlined } from '@ant-design/icons';
import type { MenuProps } from 'antd';
import { Breadcrumb, Button, Input, Layout, Menu, Table, theme } from 'antd';
import { get } from 'http';
import { getPokemonData } from './endpoints/pokemon';
import MenuItem from 'antd/es/menu/MenuItem';

const { Header, Content, Footer, Sider } = Layout;

const items1: MenuProps['items'] = ['1', '2', '3'].map((key) => ({
  key,
  label: `nav ${key}`,
}));


const TopLayer: React.FC = () => {


    const [loading, setIsLoading] = useState(false); 
    const [pokemons, setPokemons] = useState();
    const [favorite, setFavorite] = useState(false);

    const changeFavorite=()=>{
         setFavorite(true);
    }



    const columns =[
        {
            title: 'Name',
            dataIndex: 'name',
            key: 'name',
          },
          {
            title: 'Url',
            dataIndex: 'url',
            key: 'url',
          },
          {
            title: 'Favorite',
            render: (item: any) =>{
                return (
                    <div className='items-center'>
                        <Button onClick={changeFavorite}>{favorite? 
                         <StarFilled style={{color: '#c3a605f7'}}/> : <StarOutlined/> 
                         }
                        </Button>
                        </div>
                )
            }
          }
    ]

    const Filters: React.FC =()=>{
        return(
            <>
            <Input placeholder="Search Pokemons" />
            </>
        )
    }

        const getPokemons = () => {
            setIsLoading(true);
            getPokemonData({0 : Number,1000: Number })
            .then((res: any) => {
                setPokemons(res.data.results);

            })
            .catch((e: any) => {
                console.log(e)
            })
            .finally(() => setIsLoading(false));
        };

            useEffect(()=>{
                getPokemons();
            },[false])

            useEffect(()=>{
                console.log(pokemons);
            },[pokemons])
            const {
                token: { colorBgContainer },
            } = theme.useToken();

  return (
    <Layout>
      <Header style={{ display: 'flex', alignItems: 'center' }}>
        <div style={{color: "white"}} >POKEMONS</div>
      </Header>
      <Content style={{ padding: '0 50px' }}>
        <Layout style={{ padding: '24px 0', background: colorBgContainer }}>
          <Sider style={{ background: colorBgContainer }} width={200}>
            <Menu
              mode="inline"
              defaultSelectedKeys={['1']}
              defaultOpenKeys={['sub1']}
              style={{ height: '100%' }}

            >
                <MenuItem key="all" icon={React.createElement(UserOutlined)} >
                    All Pokemons
                </MenuItem>
                <MenuItem key="favorites" icon={React.createElement(UserOutlined)} >
                    Favorites
                </MenuItem>
            </Menu>
          </Sider>
          <Content style={{ padding: '0 24px', minHeight: 280 }}>
            <Filters/>
            <Table dataSource={pokemons}  columns={columns}/>
          </Content>
        </Layout>
      </Content>
      <Footer style={{ textAlign: 'center' }}>©2023 Created by Mintesnot Bekele</Footer>
    </Layout>
  );
};

export default TopLayer;
